import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";

type fetchStockType = {
  id: number;
  BAS_DD: number;
  ISU_NM: string;
  MKTCAP: string;
  MKT_NM: number;
  TDD_OPNPRC: number;
  ACC_TRDVOL: number;
};

export const fetchStockData = createAsyncThunk(
  "information/fetchStockData",
  async () => {
    const response = await axios.get("http://219.250.2.138:5018/krx/api/list/1");
    const responeData = response.data.data;
    return responeData;
  }
);

const initialState= {
  item: [],
  fetchStatus: "",
};

const itemSlice = createSlice({
  name: "items",
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder.addCase(fetchStockData.fulfilled, (state, { payload }) => {
      state.item = payload;
      state.fetchStatus = "success";
    });
  },
});

export default itemSlice.reducer;
export const selectItems = (state: any) => state.item;
