import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";

type fetchStock = {
    id: number;
    BAS_DD: number;
    ISU_NM: string;
    MKTCAP: string;
    MKT_NM: number;
    TDD_OPNPRC: number;
    ACC_TRDVOL: number;
}

const fetchStockData = createAsyncThunk(
  "information/fetchStockData",
  async (_, thunkAPI) => {
    try {
      const response = await axios.get<fetchStock>("http://219.250.2.138:5018/krx/api/list/1");
      return response.data;
    } catch (error) {
      return thunkAPI.rejectWithValue(error);
    }
  }
);

interface itemState {
  item: [];
}

const initialState = {
  item: [],
} as itemState;

const itemSlice = createSlice({
  name: "items",
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder.addCase(fetchStockData.fulfilled, (state, action) => {
      console.log(12323);
    });
  },
});

export const getStockList = itemSlice.actions;
export default itemSlice.reducer;
export const selectItems = (state: any) => state.items.item;
