import React, { useState } from "react";
import { Link } from "react-router-dom";
import styled, { keyframes } from "styled-components";

const RbContainer = styled.div`
  position: relative;
  width: 100%;
  height: 2rem;
  margin-top: 0.5rem;
  font-size: large.875rem;
  letter-spacing: -1px;
  padding: 0.25rem 1rem;
  box-sizing: border-box;
  background-color: #fff;
`;

const RbTitle = styled.div`
  font-weight: bold;
  float: left;
  padding-right: 10px;
`;

const RbWrap = styled.div`
  position: relative;
  width: auto;
  height: 100%;
  box-sizing: border-box;
  overflow: hidden;
`;

const RbUl = styled.ul`
  list-style: none;
`;

const RbLi = styled.li`
  position: absolute;
  top: -36px;
  left: 0;
`;

const RbLiPrev = styled.li`
  top: 36px;
  transition: top 0.5s ease;
`;

const RbLiCurrent = styled.li`
  top: 0;
  transition: top 0.5s ease;
`;

const RbLiNext = styled.li`
  top: -36px;
`;

const RbLink = styled(Link)`
  display: flex;
  gap: 0.25rem;
  align-items: center;
  text-decoration: none;
  -webkit-line-clamp: 1;
  -webkit-box-orient: vertical;
  overflow: hidden;
  color: #000;
`;

const animation1 = keyframes`
    0% {
        transform: translateX(0%);
    }
    50% {
        transform: translateX(-100%);
    }
    50.1% {
        transform: translateX(100%);
    }
    100% {
        transform: translateX(0%);
    }
`;

const animation2 = keyframes`
     0% {
        transform: translateX(0%);
    }
    100% {
        transform: translateX(-200%);
    }
`;

interface Props {
  title: string;
}

const RollingBannerComponent = (props: Props) => {
  const [animate, setAnimate] = useState(true);
  const onStop = () => setAnimate(false);
  const onRun = () => setAnimate(true);

  return (
    <RbContainer>
      <RbTitle>{props.title}</RbTitle>
      <RbWrap>
        <RbUl>
          <RbLiCurrent>
            <RbLink to={""}>
              <div>12분전</div>
              <div>신성델타테크는 별로네</div>
              <div>test</div>
            </RbLink>
          </RbLiCurrent>
          <RbLiNext>
            <RbLink to={""}>
              <div>1일전</div>
              <div>포스코스 가즈아!!</div>
              <div>test2</div>
            </RbLink>
          </RbLiNext>
          <RbLi>
            <RbLink to={""}>
              <div>2일전</div>
              <div>아 짜증나네</div>
              <div>test3</div>
            </RbLink>
          </RbLi>
          <RbLiPrev>
            <RbLink to={""}>
              <div>2일전</div>
              <div>완전 나락이네</div>
              <div>test4</div>
            </RbLink>
          </RbLiPrev>
        </RbUl>
      </RbWrap>
    </RbContainer>
  );
};

export default RollingBannerComponent;
