import React, { useEffect, useRef, useState } from "react";
import { item } from "./item";

interface Props {
  title: string;
}

interface IUseInterval {
  (callback: () => void, interval: number): void;
}

const useInterval: IUseInterval = (callback, interval) => {
  const saveCallback = useRef<(() => void) | null>(null);

  useEffect(() => {
    saveCallback.current = callback;
  });

  useEffect(() => {
    function tick() {
      if (saveCallback.current) {
        saveCallback.current();
      }
    }
    if (interval !== 10000) {
      let id = setInterval(tick, interval);
      return () => clearInterval(id);
    }
  }, [interval]);
};

function RollingBannerComponent(props: Props) {
  const [slideIndex, setSlideIndex] = useState(1);

  const outRef = useRef<HTMLDivElement>(null);
  const slideRef = useRef<HTMLDivElement>(null);

  const SLIDE_NUM = item.length;

  const beforeSlide = item[SLIDE_NUM - 1];
  const afterSlide = item[0];

  let copiedArr = [beforeSlide, ...item, afterSlide];

  const COPED_NUM = copiedArr.length;

  const [custominterval, setCustominterval] = useState(3000);

  useInterval(
    () => setSlideIndex((slideIndex) => slideIndex + 1),
    custominterval
  );

  if (slideIndex === 10) {
    if (slideRef.current) {
      slideRef.current.style.transition = "";
    }

    setSlideIndex(1);

    setTimeout(() => {
      if (slideRef.current) {
        slideRef.current.style.transition = "all 500ms ease-in-out";
      }
    }, 0);
  }

  const { title } = props;
  return (
    <div className="bg-white w-full flex h-10 mt-2 pl-4 pr-4 pb-2 pt-2 items-center gap-1">
      <div className="font-bold">{title}</div>
      <div className="relative w-auto h-full box-border overflow-hidden">
        <ul className="list-none animate-slider">
          <li className="flex flex-row mb-10 absolute animate-slider">
            <div className="w-100 h-full mx-10">
              <div className="font-bold">12분전</div>
              <div className="font-bold">신성델타테크는 별로네</div>
              <div className="font-bold">test</div>
            </div>
          </li>
          <li className="flex flex-row mb-10 absolute animate-slider">
            <div className="w-100 h-full mx-10">
              <div className="font-bold">1일전</div>
              <div className="font-bold">포스코스 가즈아!!</div>
              <div className="font-bold">test2</div>
            </div>
          </li>
        </ul>
      </div>
    </div>
  );
}

export default RollingBannerComponent;
