import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import {
  faChevronRight,
  faChevronDown,
} from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { useDispatch, useSelector } from "react-redux";
import { fetchStockData, pageIncrement } from "../../slice/itemSlice";
import { comma } from "../../assets/util";

interface stockDataProps {
  id: number;
  BAS_DD: number;
  ISU_NM: string;
  MKTCAP: number;
  MKT_NM: string;
  TDD_OPNPRC: number;
  ACC_TRDVOL: number;
}
interface itemProps {
  data: stockDataProps[];
  totalPage: number;
  totalData: number;
}

interface Props {
  title: string;
  isMore: boolean;
  moreLink: string;
  item: itemProps;
}

const StockItemListComponent: React.FC<Props> = ({
  title,
  isMore,
  moreLink,
  item
}) => {
  // const item = useSelector((state: any) => state.stocks.item);
  // const data = item.data;
  // const totalData: number = item.totalData;
   const dispatch = useDispatch();
  // const page: number = useSelector((state: any) => state.stocks.page);

  // useEffect(() => {
  //   dispatch(fetchStockData({ page, limit }) as any);
  // }, [dispatch, page]);

  const data = item.data;
  const totalData = item.totalData

  const moreBtn = (isMore: boolean) => {
    if (isMore === true) {
      return (
        <li className="flex gap-1 p-2 items-center border-gray-100">
          <div className="w-full text-center p-2">
            <Link to={moreLink}>
              더보기 <FontAwesomeIcon icon={faChevronRight} />
            </Link>
          </div>
        </li>
      );
    } else {
      return (
        <li className="flex gap-1 p-2 items-center border-gray-100">
          <div className="w-full text-center p-2">
            <Link to={moreLink} onClick={() => dispatch(pageIncrement())}>
              더보기 <FontAwesomeIcon icon={faChevronDown} />
              <div className="text-sm">
                {comma(data.length)} / {comma(totalData)}
              </div>
            </Link>
          </div>
        </li>
      );
    }
  };
  return (
    <div className="bg-white w-full">
      <div className="h-full mt-2 p-2 w-full border-gray-100">
        <div className="flex w-full p-2">
          <h2>{title}</h2>
        </div>
        <div className="w-full pl-2 pr-2 pb-1 flex gap-1 flex-myFlex">
          <span className="text-sm w-2/5">종목명</span>
          <span className="text-sm w-1/5 text-center">거래량</span>
          <span className="text-sm w-2/5 text-right">시가총액 ・ 현재가 </span>
        </div>
        <div className="line border-b-2 ml-2 mr-2 border-gray-100"></div>
        <ul className="pl-2 pr-2">
          {data.length > 0 ? (
            data.map((item: fetchStockType) => {
              return (
                <li
                  className="flex gap-1 p-2 items-center border-b-2 border-gray-100"
                  key={item.id}
                >
                  <div className="w-2/5 font-bold text-base">{item.ISU_NM}</div>
                  <div className="w-1/5 font-bold">
                    <div className="w-20 ml-auto mr-auto text-sm bg-blue-600 text-white text-center rounded-md max-w-xs p-2">
                      {comma(item.ACC_TRDVOL)}
                    </div>
                  </div>
                  <div className="w-2/5 text-right">
                    <div className="font-bold text-base">
                      {comma(item.MKTCAP)}
                    </div>
                    <div className="text-sm font-bold">
                      {comma(item.TDD_OPNPRC)}
                    </div>
                  </div>
                </li>
              );
            })
          ) : (
            <li className="flex gap-1 p-2 items-center border-b-2 border-gray-100">
              <div>No Datas</div>
            </li>
          )}
          {moreBtn(isMore)}
        </ul>
      </div>
    </div>
  );
};

export default StockItemListComponent;
