import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";

const fetchStockData = createAsyncThunk(
  "information/fetchStockData",
  async () => {
    const response = await axios("http://219.250.2.138:5018/krx/api/list/1");
    return response.data.data;
  }
);

interface itemState {
    item: [];
}

const initialState = {
    item: [],
} as itemState

const itemSlice = createSlice({
  name: "items",
  initialState,
  reducers: {},
  extraReducers: builder => {
    builder.addCase(fetchStockData.fulfilled,(state, action) => {
        const responeData = [];
        action.payload.forEach((val: any) => {
            responeData.push(val);
        })
        console.log(12323);
    })
  },
});

export const getStockList = itemSlice.actions;
export default itemSlice.reducer;
export const selectItems = (state: any) => state.items.item;
