import { AnyAction, combineReducers, configureStore } from "@reduxjs/toolkit";
import pagingSlice from "./slice/pagingSlice";
import ItemSlice from "./slice/itemSlice";
import { useDispatch } from "react-redux";

const combinedReducers = combineReducers({
  ItemReducer: ItemSlice.reducer,
  pagingReducer: pagingSlice,
});

export type OurStore = ReturnType<typeof combinedReducers>;

const rootReducer = (
  state: ReturnType<typeof combinedReducers>,
  action: AnyAction
) => {

  return combinedReducers(state, action);
};

export const store = configureStore<OurStore>({
  reducer: rootReducer,
});

export type AppDispatch = typeof store.dispatch;
export const useAppDispatch: () => AppDispatch = useDispatch
