import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";

type fetchStockType = {
  id: number;
  BAS_DD: number;
  ISU_NM: string;
  MKTCAP: string;
  MKT_NM: number;
  TDD_OPNPRC: number;
  ACC_TRDVOL: number;
};

export const fetchStockData = createAsyncThunk(
  "information/fetchStockData",
  async () => {
    const response = await fetch("http://219.250.2.138:5018/krx/api/list/1");
    const responeData = response.json();
    console.log(responeData);
    //return responeData;
  }
);

// interface stateProps {
//     item:any[];
//     fetchStatus:string;
// }

const initialState = {
  //item: [],
  fetchStatus: "",
};

const itemSlice = createSlice({
  name: "StockData",
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder.addCase(fetchStockData.fulfilled, (state, { payload }) => {
      //state.item = payload;
      state.fetchStatus = "success";
    });
  },
});

export default itemSlice;
export const selectItems = (state: any) => state.item;
