export function Comma(data:number) {
    if(data) {
        return data.toLocaleString('ko-KR');
    }else{
        return 0;
    }

}

export function BasdConversion(basdd:string) {
    const conversionDate = new Date(basdd);
    console.log(conversionDate);
    const year = conversionDate.getFullYear();
    const month = conversionDate.getMonth();
    const day = conversionDate.getDate();
    const result = year + "년" + month + "월" + day + "일"; 
    return result;
}
